<?phpmysql_connect ("localhost", "root", "root");	mysql_select_db("maps");$return_arr = array();	$fetch = mysql_query("SELECT * FROM location where nama like '%" . mysql_real_escape_string($_GET['term']) . "%'"); 		/* Retrieve and store in array the results of the query.*/		while ($row = mysql_fetch_array($fetch, MYSQL_ASSOC)) {		$row_array['label'] = $row['nama'];		$row_array['value'] = $row['nama'];		        array_push($return_arr,$row_array);    }/* Toss back results as json encoded array. */echo json_encode($return_arr);?>